mapper.select:
SELECT COUNT(*) FROM MEMBER_TBL
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PWD= #{memberPwd}

mapper.update:
UPDATE MEMBER_TBL
SET MEMBER_PWD = #{memberPwd }, MEMBER_NAME = #{memberName }, MEMBER_PHONE = #{memberPhone}
, MEMBER_ADDR = #{memberAddr }
WHERE MEMBER_ID = #{memberId }

DELETE FROM MEMBER_TBL

WHERE MEMBER_ID = #{memberId }
AND MEMBER_PWD = #{memberPwd}
